INSERT INTO `doc` VALUES ('5ba62c8ce44046c9a9b19ecec489e19d', 'rj.table.init', 'rj.table.init({id:\"\",toolbar...})', '<p>包装插件：bootstrapTable</p><p>目的是为了统一bootstrapTable插件的参数</p><p>调用：</p><p>&nbsp; &nbsp; &nbsp; <span style=\"font-size: small;\">&nbsp;<span style=\"font-weight: bold;\">&nbsp;rj.table.init({id:\"\",toolbar...})</span></span><br></p><p><span style=\"font-size: 1.2em;\">依赖：</span></p><p>&nbsp; &nbsp; bootstap table 插件官网<span style=\"font-size: medium; font-style: italic; font-weight: bold;\">（<a href=\"http://bootstrap-table.wenzhixin.net.cn/zh-cn/documentation/#%E8%A1%A8%E6%A0%BC%E5%8F%82%E6%95%B0\" target=\"_blank\" style=\"background-color: rgb(255, 255, 255);\">http://bootstrap-table.wenzhixin.net.cn/zh-cn/documentation/#%E8%A1%A8%E6%A0%BC%E5%8F%82%E6%95%B0</a>）</span></p>', '<table border=\"0\" width=\"100%\" cellpadding=\"0\" cellspacing=\"0\"><tbody><tr><th>名称</th><th>说明</th><th>类型</th><th>可选值</th><th>默认值</th></tr><tr><td>id</td><td>表格的选择器id</td><td>string</td><td><br></td><td><br></td></tr><tr><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><div>striped</div></divcourier></divcourier></td><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><div>是否显示行间隔色</div></divcourier></td><td>boolean</td><td><br></td><td>false</td></tr><tr><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><div>toolbar</div></divcourier></td><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\">工具按钮用哪个容器<br></divcourier></td><td>string</td><td>&nbsp;</td><td>&nbsp;</td></tr><tr><td><divcourier new\',=\"\" m