INSERT INTO `doc` VALUES ('8913dd8b716645a997984bd6f375f3ae', 'rj.form', '封装表单的获取和回显', '<p><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"></divcourier></p><div><span>封装</span><span style=\"font-size: small;\">该属性是封装表单的获取和回显</span></div><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><div>调用:</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;rj.form.get(formDom);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;rj.form.set(formDom,data);</div><div>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;get方法<span style=\"font-size: small;\">将form里的表单元素序列化成{\"username\":\"小艾\",\"age\":\"55\"}格式  </span></div><div><span style=\"font-size: small;\"><br></span></div><div><span style=\"font-size: small;\">注:表单输入控件  name和value其中有一项没值都不会序列化</span></div><div><span style=\"font-size: small;\">set方法讲这些值通过name的属性与data的key一一匹配赋值回显，包括ztree和toggleRow组件都生效</span></div></divcourier>', '<table border=\"0\" width=\"100%\" cellpadding=\"0\" cellspacing=\"0\"><tbody><tr><th>名称</th><th>说明</th><th>类型</th><th>可选值</th><th>默认值</th></tr><tr><td>formDom&nbsp;&nbsp;<br></td><td>表单的选择器</td><td><br></td><td><br></td><td><br></td></tr><tr><td>data</td><td>获取表单的数据</td><td></td><td></td><td><br></td></tr></tbody></table><p><br></p>', '2018-12-06 16:59:34', '2018-12-19 16:43:30');
INSERT INTO `doc` VALUES ('9ffb4adf59274f618cb50535d6182025', 'rj.sweetAlert', 'sweetAlert', '<h5>弹框配置，对一些操作进行相应的弹框，比浏览器内置alert弹框的使用更加方便简洁<br></h5><h5>调用： 基本写法 || 推荐写法</h5><p style=\"text-align: left;\">&nbsp; &nbsp; 弹框：rj.alert(title,text,type,fn) || xalert<br></p><p style=\"text-align: left;\">&nbsp; &nbsp; 错误提示弹框：rj.error(title,text,type,fn)&nbsp; || xerror<br></p><p style=\"text-align: left;\">&nbsp; &nbsp; 提示框：rj.confirm(title,text,type,fn)&nbsp; || xconfirm</p><p style=\"text-align: left;\">注：更多用法请参照sweetAlert插件官网（<a href=\"https://sweetalert.bootcss.com/docs/\" target=\"_blank\">https://sweetalert.bootcss.com/docs/</a><span style=\"font-size: 1.2em;\">）</span></p>', '<table border=\"0\" width=\"100%\" cellpadding=\"0\" cellspacing=\"0\"><tbody><tr><th>名称</th><th>说明</th><th>类型</th><th>可选值</th><th>默认值</th></tr><tr><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><div>title</div></divcourier></td><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\">弹窗HTML标题<br></divcourier></td><td>String</td><td><br></td><td><i>null</i>&nbsp;&nbsp;<br></td></tr><tr><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><div>text</div></divcourier></td><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\">弹窗的文本描述&nbsp;&nbsp;<br></divcourier></td><td>String</td><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><div>null&nbsp; |&nbsp; \"\"&nbsp; |&nbsp; 描述语</div></divcourier></td><td>null</td></tr><tr><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><div>type</div></divcourier><