INSERT INTO `doc` VALUES ('a9d77af5c96d4181b955ac778eba55ec', 'rj.confirm', 'rj.confirm(title,text,type,fn)', '<p><span style=\"font-size: small;\">提示框配置</span></p><p><span style=\"font-size: small;\">调用：</span></p><p><span style=\"font-size: small;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;提示框：<span style=\"font-weight: bold;\">rj.confirm(title,text,type,fn)&nbsp; || xconfirm&nbsp; &nbsp;&nbsp;</span></span></p><p><span style=\"font-size: 1.2em;\">详细使用请转到&nbsp; &nbsp;<span style=\"text-decoration-line: underline;\">&nbsp;</span></span><a href=\"#rj.sweetAlert\" target=\"_blank\" style=\"background-color: rgb(255, 255, 255); font-size: 1.2em; font-style: italic; font-weight: bold; text-decoration-line: underline;\">rj.sweetAlert</a></p><p>注：更多用法请参照sweetAlert插件官网（<a href=\"https://sweetalert.bootcss.com/docs/\" target=\"_blank\">https://sweetalert.bootcss.com/docs/</a>）&nbsp;&nbsp;<br></p>', '<p><br></p>', '2018-12-04 11:17:17', '2018-12-07 14:28:15');
INSERT INTO `doc` VALUES ('b16989020a9f453e9ece77ff9de81876', 'rj.table.fmt.VHcenter', 'rj.table.fmt.VHcenter(value,row,index)', '<div><span style=\"font-size: small;\">fmt 对象   包含格式化表格的一些方法</span></div><div><span style=\"font-size: small;\">VHcenter水平垂直居中</span></div><div><span style=\"font-size: small;\">            调用：</span></div><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\"><div>                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-weight: bold;\">&nbsp;&nbsp;<span style=\"font-size: small;\">rj.table.fmt.VHcenter</span><span style=\"font-size: small;\">(value,row,index)</span></span></div></divcourier>', '<table border=\"0\" width=\"100%\" cellpadding=\"0\" cellspacing=\"0\"><tbody><tr><th>名称</th><th>说明</th><th>类型</th><th>可选值</th><th>默认值</th></tr><tr><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\">value&nbsp;&nbsp;<br></divcourier></td><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\">当前单元格中的值&nbsp;&nbsp;<br></divcourier></td><td><br></td><td><br></td><td><br></td></tr><tr><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\">row&nbsp;&nbsp;<br></divcourier></td><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\">当前行&nbsp;&nbsp;<br></divcourier></td><td><br></td><td></td><td><br></td></tr><tr><td><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\">index&nbsp;&nbsp;<br></divcourier></td><td>当前行的下标&nbsp;&nbsp;<br></td><td>num</td><td></td><td></td></tr></tbody></table><p><br></p>', '2018-12-06 16:15:21', '2018-12-07 17: