INSERT INTO `doc` VALUES ('b4efda99c4e045f49a88c05a4364fd2f', 'rj.init', 'rj.init()', '<p>组件初始化运行的函数</p><p>组件中初始化的内容代码如下：</p><p style=\"text-align: left;\"><span style=\"font-size: small;\">&nbsp; &nbsp;&nbsp;init(){<br></span></p><divcourier new\',=\"\" monospace;font-weight:=\"\" normal;font-size:=\"\" 14px;line-height:=\"\" 19px;white-space:=\"\" pre;\"=\"\" style=\"font-size: small;\"><div style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; this.sweetAlert();</div><div style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//<span style=\"text-align: justify;\">表格插件初始化 只用初始化一次</span></div><div style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.table.tableFmt();</div><div style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.fileinput.init();</div><div style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.resetPlug();</div><div style=\"text-align: left;\">&nbsp;&nbsp;&nbsp;&nbsp;},</div></divcourier>', '<p><br></p>', '2018-12-06 16:20:57', '2018-12-07 15:13:50');
